import React from "react";

const API_KEY = "82e89009629e31d94b5e0e6bc9b6533b";

class CityWeather extends React.Component {
    state = {
        activeCity : []
    }
    componentDidMount = async () =>{

        const city = this.props.location.state.city;
        const request = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`);
        const response = await request.json();
        console.log(response);
        this.setState({activeCity : response})
    }
    render(){
        const city = this.state.activeCity;
        console.log(this.props);
        return(            
            <div>
            {	
                props.city && props.country && <p  > Location: 
                    <span  > { props.city }, { props.country }</span>
                </p> 
            }
            { 	
                props.temperature && <p  > Temperature: 
                    <span  > { props.temperature }	</span>
                </p> 
            }
            { 	
                props.humidity && <p  > Humidity: 
                    <span  > { props.humidity } </span>
                </p> 
            }
            { 	
                props.description && <p  > Conditions: 
                    <span  > { props.description } </span>
            </p> 
            }
            { 
                props.error && <p >{ props.error }</p>  
            }
            </div>
       );
    }
}

export default CityWeather;
