import React from "react";

const API_KEY = "82e89009629e31d94b5e0e6bc9b6533b";

class CityWeather extends React.Component {

    state = {
		temperature : undefined,
		city : undefined,
		country : undefined,
		humidity : undefined,
		description : undefined,
		error :undefined
    }	
  
    getWeather = async () => {
        const city = this.props.location.state.city;
		const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`);
	 	const data = await api_call.json();
	 	console.log(data);
	 	if (city){
	 		this.setState({
	 		temperature : data.main.temp,
	 		city : data.name,
	 		country : data.sys.country,
			humidity : data.main.humidity,
			description : data.weather[0].description
	 	});

	 	} else {
	 		this.setState({
	 		temperature : undefined,
	 		city : undefined,
	 		country : undefined,
			humidity : undefined,
			description : undefined
			});

	 	}
	 	
	}

    state = {
        activeCity : []
    }
    componentDidMount = async () =>{
        const city = this.props.location.state.city;
        const request = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`);
        const response = await request.json();
        console.log(response);
        this.setState({activeCity : response})
    }
    render(){
        const city = this.state.activeCity;
        return(            
            <div>
            {	
                city.name && city.sys.country && <p  > Location: 
                    <span  > { city.name }, { city.sys.country }</span>
                </p> 
            }
            { 	
                city.name && <p  > Current Temperature: 
                    <span  > { city.main.temp } &deg;C	</span>
                </p> 
            }
            { 	
                city.name && <p  > Minimum Temperature: 
                    <span  > { city.main.temp_min } &deg;C	</span>
                </p> 
            }
            { 	
                city.name && <p  > Maximum Temperature: 
                    <span  > { city.main.temp_max } &deg;C	</span>
                </p> 
            }
            
            { 	
                city.name && <p  > Humidity: 
                    <span  > { city.main.humidity }%</span>
                </p> 
            }
            { 	
                city.name && <p  > Conditions: 
                    <span  > { city.weather[0].description } </span>
            </p> 
            }
            
            </div>
       );
    }
}

export default CityWeather;
