import React from 'react';
import ReactCountryFlag from "react-country-flag";
import {Link} from "react-router-dom";
import Display from "./display";

const API_KEY = "82e89009629e31d94b5e0e6bc9b6533b";

class Title extends React.Component {
  componentDidMount = async () => {        
    const city = "bangalore";
    const req = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`);
    const temp = await req.json();
    console.log(temp);
    const temp_bang = temp.main.temp;
  }
  render() {
    return (
      <div className="title-container"> 
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      
      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Please click on the city know the weather 
      <br></br>
      <br></br>
      
      <Display className="city"/>
      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
      <ReactCountryFlag styleProps={{width: '20px',height: '20px'}} className="country" code="in" svg/>
      &nbsp;&nbsp;
        <Link to={{ pathname: `/temp/bangalore`, state :{city:"bangalore"} }}
        className="city" 
        style={{ textDecoration: 'none' }} >
        Bangalore, India {this.props.temp_bang}
        </Link>  
      <br></br>
      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
      <ReactCountryFlag styleProps={{width: '20px',height: '20px'}} className="country" code="ke" svg/>
      &nbsp;&nbsp;
        <Link to={{ pathname: `/temp/nairobi`, state :{city:"nairobi"} }}  
            className="city" 
            style={{ textDecoration: 'none' }}>
        Nairobi, Kenya
        </Link>  
      <br></br>
      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
      <ReactCountryFlag styleProps={{width: '20px',height: '20px'}} className="country" code="se" svg/>
      &nbsp;&nbsp;
        <Link to={{ pathname: `/temp/stockholm`, state :{city:"stockholm"} }}
        className="city" 
        style={{ textDecoration: 'none' }}>
          Stockholm, Sweden
        </Link>  
      </div>
    );
  }
}

export default Title;
