import React from "react";




class Precautions extends React.Component {


    Precaution(condition ) {
        switch(condition) {
            case 'Clear':
                return "Clear" ;
            case 'Clouds':
                return 'Clouds';
            case 'Rain':
                return 'Rain';
            case 'Clear':
                return "Clear" ;
            case 'Clouds':
                return 'Clouds';
            case 'Rain':
                return 'Rain';
            default:
            return null;
        
        }
      }



    render() {
        
        var condition = this.props.main ;
        var precaution = this.Precaution(condition);
        
        return (
            <div>
            
                <p>
                  {precaution}
                </p> 
            
            </div>
        );
    }
}

     
 export default Precautions;
