import React from 'react';
import Maps from "./components/Maps";
import "./App.css"

var locations =  {
  name: this.props.city,
  lat: this.props.lat,
  lng: this.props.lon
};


class App extends React.Component {
	/**use async to make htttp call very easy* */
  
  render() {         
    return (
      <div className ="wrapper" >
        <Title />
          <Maps  
              id="myMap"
              options={{
                center: { 
                  lat: 38.9637,
                  lng: 35.2433 }, 
                zoom: 2
              }}
              
              onMapLoad={map => 
                { 
                  var marker = locations.map(function (location, i) {

                  return new window.google.maps.Marker({
                    position: location,
                    map:map,
                    animation: window.google.maps.Animation.DROP,
                    title : location.name,
                    icon: "https://c.tadst.com/gfx/w//40/wt-33.png",
                    label: location.name  
                  });
                });
                var contentString =  "hi"
                var infowindow = new window.google.maps.InfoWindow();
                window.google.maps.event.addListener(marker,'click', (function(marker,content,infowindow){ 
                  return function() {
                      infowindow.setContent(content);
                      infowindow.open(map,marker);
                  };
              })(marker,contentString,infowindow));
            }}    
          />       
      {this.props.city}
      </div>
    );
  }
}

export default App;
