class Precautions extends React.Component {


    Precaution({ condition }) {
        switch(condition) {
          case 'Clear':
            return "jhds" ;
          case 'Clouds':
            return <Warning text={text} />;
          case 'Rain':
            return <Error text={text} />;
          default:
            return null;
        }
      }



    render() {
        const condition = this.props.main ;
        console.log(this.props)
        return (
            <div>
            
                <p>
                  {this.props.main}
                </p> 
            
            </div>
        );
    }
}

     
 export default Precautions;
