import React from 'react';


import Title from "./components/Title";
import Maps from "./components/Maps";
import "./App.css"



const API_KEY = "82e89009629e31d94b5e0e6bc9b6533b";

var locations = [{
  name: "Nairobi",
  lat: -1.28333,
  lng: 36.81667

}, {
  name: "Bangalore",
  lat: 12.9716,
  lng: 77.5946
}, {
  name: "Stockholm",
  lat: 59.3293,
  lng: 18.0686
}];



class App extends React.Component {
	/**use async to make htttp call very easy* */
	state = {
		temperature : undefined,
		city : undefined,
		country : undefined,
		humidity : undefined,
		description : undefined,
		error :undefined
  }	

  getWeather = async (e) => {
		e.preventDefault();
		const city = e.target.elements.city.value;
		const country = e.target.elements.country.value;
		const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${API_KEY}&units=metric`);
	 	const data = await api_call.json();
	 	console.log(data);
	 	if (city && country){
	 		this.setState({
	 		temperature : data.main.temp,
	 		city : data.name,
	 		country : data.sys.country,
			humidity : data.main.humidity,
			description : data.weather[0].description,
			error : ""

	 	});

	 	} else {
	 		this.setState({
	 		temperature : undefined,
	 		city : undefined,
	 		country : undefined,
			humidity : undefined,
			description : undefined,
			error : "Please enter the city and the country"
			});

	 	}

	}

  render() {    


    return (
      <div className ="wrapper" >
        <Title />
          <Maps  
              id="myMap"
              options={{
                center: { 
                  lat: 38.9637,
                  lng: 35.2433 }, 
                zoom: 2
              }}

              onMapLoad={map => 
                { 
                  var marker = locations.map(function (location, i) {

                  return new window.google.maps.Marker({
                    position: location,
                    map:map,
                    animation: window.google.maps.Animation.DROP,
                    title : location.name,
                    icon: "https://c.tadst.com/gfx/w//40/wt-33.png",
                    label: location.name  
                  });
                });
                var contentString =  "hi"
                var infowindow = new window.google.maps.InfoWindow();
                window.google.maps.event.addListener(marker,'click', (function(marker,content,infowindow){ 
                  return function() {
                      infowindow.setContent(content);
                      infowindow.open(map,marker);
                  };
              })(marker,contentString,infowindow));
            }}    
          />       

      </div>
    );
  }
}

export default App;
