import React from "react";
import Precautions from "./Precautions"

const API_KEY = "82e89009629e31d94b5e0e6bc9b6533b";


class CityWeather extends React.Component {

    state = {
        temperature : undefined,
        lowest_temp: undefined,
        highest_temp: undefined,
		city : undefined,
		country : undefined,
		humidity : undefined,
		description : undefined,
		main :undefined
    }	
  
    componentDidMount = async () => {        
        const city = this.props.location.state.city;
		const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`);
	 	const data = await api_call.json();
	 	console.log(data);
	 	if (city){
	 		this.setState({
            temperature : data.main.temp,
            lowest_temp: data.main.temp_min,
            highest_temp: data.main.temp_max,
	 		city : data.name,
	 		country : data.sys.country,
			humidity : data.main.humidity,
            description : data.weather[0].description,
            main : data.weather[0].main
	 	});

	 	} else {
	 		this.setState({
            temperature : undefined,
            lowest_temp: undefined,
            highest_temp: undefined,
	 		city : undefined,
	 		country : undefined,
			humidity : undefined,
            description : undefined,
            main: undefined
			});

	 	}
	 	
	}
    render(){
        return(            
            <div className="detail_wrap " >
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            {	
                this.state.city && <p className="weather__key"   > &nbsp; &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; Location: 
                    <span   >  { this.state.city }, { this.state.country }</span>
                </p> 
            }            
            { 	
                this.state.temperature && <p className="weather__key"    > &nbsp; &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;Current Temperature: 
                    <span    > { this.state.temperature} &deg;C	</span>
                </p> 
            }
            
            { 	
                this.state.lowest_temp && <p className="weather__key"    >&nbsp; &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; Minimum Temperature: 
                    <span  > { this.state.lowest_temp } &deg;C	</span>
                </p> 
            }
            { 	
                this.state.highest_temp && <p className="weather__key"    >&nbsp; &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; Maximum Temperature: 
                    <span   > { this.state.highest_temp} &deg;C	</span>
                </p> 
            }
            
            { 	
                this.state.humidity && <p className="weather__key"   >&nbsp; &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; Humidity: 
                    <span  > { this.state.humidity }%</span>
                </p> 
            }
            { 	
                this.state.description && <p className="weather__key"   > &nbsp; &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;Conditions: 
                    <span    > {  this.state.description} </span>
            </p> 
            }
            
            <Precautions 
                    main={this.state.main}
                    temperature = {this.state.temperature}
            />
            </div>
       );
    }
}


export default CityWeather;
