import React,{Component} from "react";
import Card from "./CardUi";
import stockholm from "../img/stockholm.PNG";
import bangalore from "../img/bangalore.PNG";
import nairobi from "../img/nairobi.PNG";


const API_KEY = "82e89009629e31d94b5e0e6bc9b6533b";

class Cards extends React.Component {
    /**use async to make htttp call very easy* */
    state = {
        activeTemp:[]
      }
  
    render() {         
    return (
        <div className="container-fluid d-flex justify-content-center">
            <div className="row">
                <div className="col-md-4">
                    <Card imgsrc={bangalore} title="Bangalore,India"
                    gist="Bengaluru is the capital of India's  Karnataka state.
                     The center of India's high-tech industry, the city is also known for its parks" />
                </div>
                <div className="col-md-4">
                    <Card imgsrc={nairobi}  title="Nairobi, Kenya"
                    gist = "Nairobi is Kenyaâ€™s capital city.Nairobi is also often used as a jumping-off point for safari trips elsewhere in Kenya."/>
                </div>
                <div className="col-md-4">
                    <Card imgsrc={stockholm}  title="Stockholm, Sweden"
                     gist = "Stockholm, the capital of Sweden, encompasses 14 islands and more than 50 bridges on an extensive Baltic Sea archipelago."/>
                </div>
            </div>
        </div>


    );
  };
}

export default Cards;
